INSERT INTO SCHEMA.CTTEXTES (CDIELE, CDITXT, CDLCIL, CDLDES, PSPY) VALUES (71500000005+ELE_BASE+3, 71500000005+TEX_BASE+7, 'de', '[DE]Conformément à la loi fédérale du 25 juin 1982 sur la prévoyance professionnelle vieillesse, survivants et invalidité (LPP), tous les employeurs occupant des salariés soumis à l¬assurance obligatoire doivent être affiliés à une institution de prévoyance inscrite dans le registre de la prévoyance professionnelle <style isItalic=¬true¬>(art. 11, al.1 LPP)</style>. Les Caisses de compensation AVS sont chargées du contrôle de ces dispositions.', '20171030151307ccjuglo');
INSERT INTO SCHEMA.CTTEXTES (CDIELE, CDITXT, CDLCIL, CDLDES, PSPY) VALUES (71500000005+ELE_BASE+3, 71500000005+TEX_BASE+8, 'fr', 'Conformément à la loi fédérale du 25 juin 1982 sur la prévoyance professionnelle vieillesse, survivants et invalidité (LPP), tous les employeurs occupant des salariés soumis à l¬assurance obligatoire doivent être affiliés à une institution de prévoyance inscrite dans le registre de la prévoyance professionnelle <style isItalic=¬true¬>(art. 11, al.1 LPP))</style>. Les Caisses de compensation AVS sont chargées du contrôle de ces dispositions.', '20171030151307ccjuglo');
INSERT INTO SCHEMA.CTTEXTES (CDIELE, CDITXT, CDLCIL, CDLDES, PSPY) VALUES (71500000005+ELE_BASE+3, 71500000005+TEX_BASE+9, 'it', '', '20171030151307ccjuglo');
