INSERT INTO SCHEMA.CTTEXTES (CDIELE, CDITXT, CDLCIL, CDLDES, PSPY) VALUES (71500000005+ELE_BASE+4, 71500000005+TEX_BASE+10, 'de', '[DE]Or vous avez occupé des salariés soumis à l¬assurance obligatoire, ainsi qu¬en témoigne l¬extrait de votre (vos) déclaration(s) et ne nous avez pas remis l¬attestation de votre assureur LPP.', '20171030130631ccjuglo');
