INSERT INTO SCHEMA.CTTEXTES (CDIELE, CDITXT, CDLCIL, CDLDES, PSPY) VALUES (71500000005+0+7, 71500000005+0+20, 'fr', 'Dans l¬attente de votre réponse, nous vous prions d¬agréer, {0}, l¬expression de nos sentiments distingués.', '20171030130600ccjuglo');
